const POST_TYPE = 'ADD-POST'
const SEND_MESSAGE = 'SEND-MESSAGE'

const UPDATE_POST_TEXT = 'UPDATE-TEXT-POST'
const UPDATE_POST_THEME = 'UPDATE-THEME-POST'
const UPDATE_MESSAGE_TEXT = 'UPDATE-MESSAGE-TEXT'

let store = {
    _state: {

        profilePage: {

            myPosts: [
                {id: 1, theme: "Как мои дела?", content: "Хей! Мои дела прекрасны, сегодня я купила себе новый телефон!", likeCount: 12},
                {id: 2, theme: "Отдыхаю...", content: "Сегодня я с семьез поехала в горы где провела круто время.", likeCount: 8},
                {id: 3, theme: "Новый день!", content: "Доброе утро, настал новый день. Вперед к свершениям!", likeCount: 325}
            ],

            friendsOnline: [
                {id: 1, name: "Богдан", gen: "m"},
                {id: 2, name: "Саша", gen: "w"},
                {id: 3, name: "Эмилия", gen: "w"},
                {id: 4, name: "Никита", gen: "m"},
                {id: 5, name: "Ксюша", gen: "w"}
            ],


            postNewTheme: "theme",

            postNewText: "text"


        },

        dialogsPage: {

            dialogs: [
                {id: 1, name: "Викулин Богдан", gen: "m"},
                {id: 2, name: "Саша Протасова", gen: "w"},
                {id: 3, name: "Никита Бондаренко", gen: "m"},
                {id: 4, name: "Эмилия Бондаренко", gen: "w"}
            ],

            messages: [
                {id: 1, msg: "Привет!", who: "Богдан", gen: "m" },
                {id: 2, msg: "Приветик, что делаешь?", who: "Я", gen: "w" },
                {id: 3, msg: "Учу React.js. А ты?", who: "Богдан", gen: "m" },
                {id: 4, msg: "Делаю уроки, как твои дела?", who: "Я", gen: "w" },
                {id: 6, msg: "Ладненько мне пора идти)", who: "Я", gen: "w" },
                {id: 7, msg: "Давай))", who: "Богдан", gen: "m" }
            ],

            messageText: "Hi!"
        }

    },
    _subscribe() {
        alert("Subscribe don't work!")
    },

    subscribe(observe) {
        this._subscribe = observe
    },
    getState() {
        return this._state
    },

    dispatch(action) {

        if(action.type === "ADD-POST") {

            let post = {
                id: 5,
                theme: this._state.profilePage.postNewTheme,
                content: this._state.profilePage.postNewText,
                likeCount: 0
            }

            this._state.profilePage.postNewText = ''
            this._state.profilePage.postNewTheme = ''

            this._state.profilePage.myPosts.push(post)
            this._subscribe(this._state)

        } else if (action.type === "UPDATE-TEXT-POST") {

            this._state.profilePage.postNewText = action.text
            this._subscribe(this._state)

        } else if (action.type === "UPDATE-THEME-POST") {

            this._state.profilePage.postNewTheme = action.theme
            this._subscribe(this._state)

        } else if (action.type === "UPDATE-MESSAGE-TEXT") {

            this._state.dialogsPage.messageText = action.text
            this._subscribe(this._state)

        } else if (action.type === "SEND-MESSAGE") {
            debugger

            let message = {
                id: this._state.dialogsPage.messages + 1,
                msg: this._state.dialogsPage.messageText,
                who: "Я",
                gen: "w"
            }

            this._state.dialogsPage.messageText = ''

            this._state.dialogsPage.messages.push(message)
        }
    }
}

export const addPostActionCreator = () => ({type: POST_TYPE})
export const addMsgActionCreator = () => ({type: SEND_MESSAGE})
export const updateTextPostActionCreator = (text) => ({type: UPDATE_POST_TEXT, text: text})
export const updateMessageActionCreator = (msgText) => ({type: UPDATE_MESSAGE_TEXT, text: msgText})
export const updateThemePostActionCreator = (theme) => ({type: UPDATE_POST_THEME, theme: theme})


export default store
