const SEND_MESSAGE = 'SEND-MESSAGE'
const UPDATE_MESSAGE_TEXT = 'UPDATE-MESSAGE-TEXT'

const dialogsReducer = (state, action) => {

    if (action.type === UPDATE_MESSAGE_TEXT) {
        state.dialogsPage.messageText = action.text
    } else if (action.type === SEND_MESSAGE) {
        debugger

        let message = {
            id: state.dialogsPage.messages.length + 2,
            msg: state.dialogsPage.messageText,
            who: "Я",
            gen: "w"
        }

        state.dialogsPage.messageText = ''
        state.dialogsPage.messages.push(message)
    }

    return state
}

export default dialogsReducer
