import React from 'react'
import s from './AddPost.module.css'
import Button from '../../../Buttons/Button'
import {addPostActionCreator, updateTextPostActionCreator, updateThemePostActionCreator} from "../../../../redux/store";

const AddPost = (props) => {

    let themePost = React.createRef()
    let textPost = React.createRef()
    let action

    let addPost = () => {
        action = addPostActionCreator()
        props.dispatch( action )
    }

    let changeTextPost = () => {
        let text = textPost.current.value
        action = updateTextPostActionCreator(text)
        props.dispatch( action )
    }

    let changeThemePost = () => {
        let theme = themePost.current.value
        action = updateThemePostActionCreator(theme)

        props.dispatch( action )
    }


    return (
        <div className={s.add_posts}>
            <span className={s.add_post}>Добавить новость:</span>
            <input type="text"
                   placeholder="Тема новости"
                   ref={ themePost }
                   value={ props.newTheme }
                   onChange={ changeThemePost }/>

            <textarea placeholder="Ваши новости..."
                      ref={ textPost }
                      value={ props.newText }
                      onChange={changeTextPost}/>


            <Button name="Опубликовать" fn={ addPost }/>
        </div>
    )
}

export default AddPost
