import React from 'react'
import s from './AddPost.module.css'
import Button from '../../../Buttons/Button'
import {renderApp} from "../../../../render";

const AddPost = (props) => {

    let themePost = React.createRef();
    let textPost = React.createRef();

    let addPost = () => {

        let theme = themePost.current.value
        let text = textPost.current.value

        themePost.current.value = null
        textPost.current.value = null

        props.addPost(theme, text)
    }


    return (
        <div className={s.add_posts}>
            <span className={s.add_post}>Добавить новость:</span>
            <input type="text" placeholder="Тема новости" ref={ themePost } value={ props.newThemePost }/>
            <textarea placeholder="Ваши новости..." ref={ textPost } value={ props.newTextPost }/>
            <Button name="Опубликовать" fn={ addPost }/>
        </div>
    )
}

export default AddPost
