        },

        dialogsPage: {

            dialogs: [
                {id: 1, name: "Викулин Богдан", gen: "m"},
                {id: 2, name: "Саша Протасова", gen: "w"},
                {id: 3, name: "Никита Бондаренко", gen: "m"},
                {id: 4, name: "Эмилия Бондаренко", gen: "w"}
            ],

            messages: [
                {id: 1, msg: "Привет!", who: "Богдан", gen: "m" },
                {id: 2, msg: "Приветик, что делаешь?", who: "Я", gen: "w" },
                {id: 3, msg: "Учу React.js. А ты?", who: "Богдан", gen: "m" },
                {id: 4, msg: "Делаю уроки, как твои дела?", who: "Я", gen: "w" },
                {id: 6, msg: "Ладненько мне пора идти)", who: "Я", gen: "w" },
                {id: 7, msg: "Давай))", who: "Богдан", gen: "m" }
            ]
        }

    },
    _subscribe() {
        alert("Subscribe don't work!")
    },

    subscribe(observe) {
        this._subscribe = observe
    },
    getState() {
        return this._state
    },

    dispatch(action) {

        if(action.type === "ADD-POST") {

            let post = {
                id: 5,
                theme: this._state.profilePage.postNewTheme,
                content: this._state.profilePage.postNewText,
                likeCount: 0
            }

            this._state.profilePage.postNewText = ''
            this._state.profilePage.postNewTheme = ''

            this._state.profilePage.myPosts.push(post)
            this._subscribe(this._state)

        } else if (action.type === "UPDATE-TEXT-POST") {

            this._state.profilePage.postNewText = action.text
            this._subscribe(this._state)

        } else if (action.type === "UPDATE-THEME-POST") {

            this._state.profilePage.postNewTheme = action.theme
            this._subscribe(this._state)

        }
    }
}


export default store
