const ADD_POST = 'ADD-POST';
const UPDATE_POST_TEXT = 'UPDATE-TEXT-POST';
const UPDATE_POST_THEME = 'UPDATE-THEME-POST';

const profileReducer = (state, action) => {
  debugger;

  switch (action.type) {
    case ADD_POST:
      let post = {
        id: 5,
        theme: state.postNewTheme,
        content: state.postNewText,
        likeCount: 0
      };
      state.postNewText = '';
      state.postNewTheme = '';
      state.myPosts.push(post);
      return state;

    case UPDATE_POST_THEME:
      state.postNewTheme = action.text;
      return state;

    case UPDATE_POST_TEXT:
      state.postNewText = action.theme;
      return state;

    default:
      return state;
  }
};

export default profileReducer;
export const updateThemePostActionCreator = theme => ({
  type: UPDATE_POST_THEME,
  theme: theme
});
export const addPostActionCreator = () => ({
  type: ADD_POST
});
export const updateTextPostActionCreator = text => ({
  type: UPDATE_POST_TEXT,
  text: text
});
