import React from 'react'
import s from './Dialogs.module.css'
import Button from "../Buttons/Button";
import {NavLink} from "react-router-dom";


const Dialog = (props) => {
    let ava = props.img
    let path = "/dialogs/id" + props.id

    if(props.img == "" || props.img == null) {
        if(props.gen == "w") ava = "https://image.flaticon.com/icons/svg/145/145847.svg"
        else ava = "https://image.flaticon.com/icons/svg/145/145849.svg"
    }

    return (
        <div className={s.dialog}>
            <NavLink to={path}>
                <div className={s.ava}>
                    <img src={ava} />
                </div>
                <h3 className={s.name}>{props.name}</h3>
            </NavLink>
        </div>
    )
}

const Message = (props) => {

    let ava = props.img

    if(props.img == "" || props.img == null) {
        if(props.gen == "w") ava = "https://image.flaticon.com/icons/svg/145/145847.svg"
        else ava = "https://image.flaticon.com/icons/svg/145/145849.svg"
    }

    return (
        <div className={s.message_wrap} value={props.who}>
            <img src={ava} alt=""/>

            <div className={s.message}>
                <div className={s.text}>{props.msg}</div>
                <div className={s.time}></div>
            </div>

            <img src={ava} alt=""/>
        </div>
    )
}

const Dialogs = () => {

    let dialogs =[
        {id: 1, name: "Викулин Богдан", gen: "m"},
        {id: 2, name: "Саша Протасова", gen: "w"},
        {id: 3, name: "Никита Бондаренко", gen: "m"},
        {id: 4, name: "Эмилия Бондаренко", gen: "w"}
    ]

    let Messages = [
        {id: 1, msg: "Привет!", who: "Богдан", gen: "m" },
        {id: 2, msg: "Приветик, что делаешь?", who: "Я", gen: "w" },
        {id: 3, msg: "Учу React.js. А ты?", who: "Богдан", gen: "m" },
        {id: 4, msg: "Делаю уроки, как твои дела?", who: "Я", gen: "w" },
        {id: 6, msg: "Ладненько мне пора идти)", who: "Я", gen: "w" },
        {id: 7, msg: "Давай))", who: "Богдан", gen: "m" }
    ]
    return (
       <main>
            <div className={s.dialogs}>
                <div className={s.dialog_items}>
                    {
                        dialogs.map(item => <Dialog name={item.name} id={item.id} gen={item.gen}/>)
                    }
                </div>

                <div className={s.messages} value="inter">


                    <div className={s.scroll} id="scroll">
                        {
                            Messages.map(item => <Message id={item.id} who={item.who} msg={item.msg} gen={item.gen}/>)
                        }
                    </div>


                    <div className={s.field}>
                        <textarea placeholder="Напишите сообщение..."></textarea>
                        <Button name="Отправить"/>
                    </div>

                </div>
            </div>
       </main>
    )
}

export default Dialogs
